import groovy.xml.XmlSlurper

// Path to your XML file
def xmlFilePath = 'C:/Users/z004xtmc/Prathamesh/SOAP_UI/SOAPProjects/Resources/Data Files/data.xml'

// Read the XML file
def xmlFile = new File(xmlFilePath)
if (!xmlFile.exists()) {
    log.error "XML file not found at path: ${xmlFilePath}"
    return
}

// Parse the XML file
def xml = new XmlSlurper().parse(xmlFile)

// Iterate over each record and use the data
xml.record.a.each { record1 ->
    def name = record1.name.text()
    def email = record1.email.text()
    def age = record1.age.text()

    // Log the data
    log.info "Name: ${name}, Email: ${email}, Age: ${age}"

}
